c-----------------------------papcont2------------------------------cc     This program controls the calculation of the CONTinuumc     x-ray FLuoRescence correction for thin film and bulk systemc     standards and specimens. cc     !!!P&P surface point model in Electron Probe Quantitation!!!c     ============================================================ cc     program completed on 6/93 by richard a. waldoc      subroutine papcont2(delta,mode,layer,ma,chia,d1,d2,je)      real delta,je,ma      character*1 mode      if (mode.eq.'B') then        je=alog(1+chia/ma)      else if (layer.eq.1) then        je=exp(-chia*delta)*ei(-d2)-ei(-d2*(1+chia/ma))+alog(1+chia/ma)      else if (layer.eq.7) then        je=-ei(-d1)+exp(chia*d1/ma)*ei(-d1*(1.+chia/ma))      else          je=-ei(-d1)+exp(chia*d1/ma)*ei(-d1*(1.+chia/ma))+     &       exp(-chia*delta)*ei(-d2)-     &       exp(chia*d1/ma)*ei(-d2*(1.+chia/ma))      endif      je=je/chia      return      end