c-----------------------------RJUMP----------------------------------c JUMP Ratiosc 3/91 r.a. waldoc      function rjump(z,nedge)      real rjump1(92),rjump4(92)      DATA rjump1/30.0,30.0,29.0,28.0,     &  27.0,26.22,25.76,24.0,19.33,19.,18.40,     &  15.0,14.0,12.96,12.32,11.67,11.12,11.,     &  10.10,9.60,9.29,8.87,8.703,8.497,8.24,     &  8.108,7.962,7.769,7.659,7.505,7.376,7.299,     &  7.22,7.18,7.142,7.0,7.018,6.976,6.95,     &  6.96,7.035,6.94,6.5,6.049,6.58,6.95,     &  6.73,6.855,6.89,5.92,5.80,5.80,5.70,     &  5.6,5.50,5.50,5.50,5.50,5.4,5.30,     &  9*5.0,4.75,4.70,4.70,4.70,4.70,     &  4.70,4.60,4.60,4.60,4.60,4.60,4.50,     &  5.60,4.50,9*4.20/      DATA rjump4/28*6.0,5.73,5.0,4.246,4.605,     &  4.661,4.583,4.524,4.5,4.39,4.314,4.24,     &  4.166,4.123,3.979,3.9,3.897,3.839,3.80,     &  3.71,3.72,3.61,3.539,3.46,3.403,3.39,     &  3.3,3.251,3.23,3.168,3.133,3.1,3.044,     &  4*3.0,2.86,2.83,3*2.8,2.691,2.65,2.64,2.615,2.60,     &  2.574,2.544,2.52,2.48,2.47,2.438,2.404,     &  2.394,2.36,6*2.2,1.94,2.0,2.069/      nz=int(z)      if (nedge.eq.2) rjump=1.17      if (nedge.eq.3) rjump=1.39      if (nedge.eq.5) rjump=1.16      if (nedge.eq.6) rjump=1.207      if (nedge.eq.7) rjump=1.158      if ((nedge.eq.8).or.(nedge.eq.9)) rjump=1.895      if (nedge.gt.9) rjump=2.0      if (nedge.eq.1) rjump=rjump1(nz)      if (nedge.eq.4) rjump=rjump4(nz)      return      end